package pk.codeapp.methods;  /**  *  * @author Daniel Amador  */ public class Dupla {     double posX;     double posY;      public Dupla(double posX, double posY)     {         this.posX = posX;         this.posY = posY;     }           public double getPosX()     {         return posX;     }      public void setPosX(double posX)     {         this.posX = posX;     }      public double getPosY()     {         return posY;     }      public void setPosY(double posY)     {         this.posY = posY;     }      } 